import { formatDateProperty } from '@adminjs/design-system';
export default ((value, propertyType) => {
  if (!value) {
    return '';
  }
  const date = propertyType === 'date' ? new Date(`${value}T00:00:00`) : new Date(value);
  if (date) {
    return formatDateProperty(date, propertyType);
  }
  return '';
});